package jml.ot;

public class RedSands {
	
	public static void main(String[] args)
	{
		System.out.println("starting redsands...");
		while(true)
		{
			try 
			{
				Thread.sleep(1000);
			} 
			catch (InterruptedException e) 
			{
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}
	}

}
